const lang=document.querySelector("html").getAttribute("lang");function toggle(e,t){if(e)return e.classList.toggle("active",t)}const articles=document.querySelectorAll("article");articles.forEach((e=>{const t=e.querySelector("a"),o=e.querySelector(".body");t.onclick=e=>{toggle(o)||e.preventDefault()},toggle(o,!1)}));const links=document.querySelectorAll("#sidebar a");links.forEach((e=>{const t=e.getAttribute("href").substring(e.getAttribute("href").indexOf("#")+1),o=document.getElementById(t),c=o.closest("article");o.setAttribute("target","_self"),e.onclick=()=>{if(c){toggle(c.querySelector(".body"),!0)}setTimeout((()=>{c?c.scrollIntoView({behavior:"smooth"}):o.scrollIntoView({behavior:"smooth"})}),500)};document.querySelectorAll(`.body a[href="${lang}/#${t}"]`).forEach((t=>{t.setAttribute("target","_self"),t.onclick=()=>{e.click()}}))}));const show=document.querySelector("button.control.show"),hide=document.querySelector("button.control.hide");show.onclick=()=>{articles.forEach((e=>{toggle(e.querySelector(".body"),!0)}))},hide.onclick=()=>{articles.forEach((e=>{toggle(e.querySelector(".body"),!1)}))},window.onload=()=>{const e=window.location.hash.substring(1);if(e){const t=window.location.pathname.substring(window.location.pathname.indexOf("/",1)+1);document.querySelector(`#sidebar a[href="${t}#${e}"]`).click()}};
